import React from "react";
import profileImg from "../assets/profile.jpeg"; // Import your profile image

function Home() {
  return (
    <section id="home" className="min-h-screen flex flex-col justify-center items-center bg-blue-50">
      <img
        src={profileImg}
        alt="Pavithran Gnanasekaran"
        className="w-32 h-32 rounded-full mb-6 shadow-lg object-cover"
      />
      <h2 className="text-4xl font-bold mb-4">Hi, I'm Pavithran Gnanasekaran</h2>
      <p className="text-xl mb-6">M.S. Computer Science | AI & ML Enthusiast</p>
      <div className="flex space-x-4">
        <a href="#projects" className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">View Projects</a>
        <a href="mailto:pavithrangnanasekaran@gmail.com" className="px-6 py-2 border border-blue-600 text-blue-600 rounded-lg hover:bg-blue-100">Contact Me</a>
      </div>
    </section>
  );
}

export default Home;
